mapper.select:
SELECT id, short_code, long_url, created_at, expire_at, visit_count
FROM short_url
WHERE short_code = #{shortCode}

mapper.update:
UPDATE short_url
SET visit_count = visit_count + 1
WHERE short_code = #{shortCode}

mapper.select:
SELECT COUNT(*)
FROM short_url
WHERE short_code = #{shortCode}
